import React, { Component } from "react";
import "./input.css";

class Input extends Component {
  // returns true if val is a number, a period, or equals sign
  isOperator = (val) => {
    return !isNaN(val) || val === "." || val === "=";
  };

  render() {
    return (
      <div className="input">
      </div>
    );
  }
}

export default Input;
